import Vue from 'vue'
import Router from 'vue-router'

Vue.use(Router)

/* Layout */
import Layout from '@/layout'

export const constantRoutes = [{
    path: '/login',
    component: () => import('@/views/login/index'),
    hidden: true
  },
  {
    path: '/',
    component: Layout,
    redirect: '/dashboard',
    name: '/',
    meta: {
      title: ''
    },
    children: [{
      path: 'dashboard',
      name: 'Dashboard',
      component: () => import('@/views/dashboard/index'),
      meta: {
        title: 'Dashboard',
        icon: 'dashboard',
        rights: {
          V: '1',
          U: '1',
          VPic: '1',
          UPrint: '1'
        }
      }
    }]
  }
]

export const asyncRoutes = [{
    path: '/history',
    component: Layout,
    redirect: '/history/record',
    name: 'History',
    meta: {
      title: 'History',
      icon: 'el-icon-s-order'
    },
    children: [{
      path: 'record',
      component: () => import('@/views/history/record'), // Parent router-view
      name: 'Record',
      meta: {
        title: 'Record',
        icon: 'visitor-record',
        rights: {
          V: '1',
          VPic: '1',
          Ex: '1'
        },
        keepAlive: true
      }
    }]
  },
  {
    path: '/configuration',
    component: Layout,
    redirect: '/configuration/visitplace',
    name: 'Configuration',
    meta: {
      title: 'Configuration',
      icon: 'configuration'
    },
    children: [{
      path: 'visitplace',
      component: () => import('@/views/configuration/visitplace'), // Parent router-view
      name: 'VisitPlace',
      meta: {
        title: 'VisitPlace',
        icon: 'visitplace',
        rights: {
          V: '1',
          C: '1',
          U: '1',
          D: '1'
        }
      }
    }]
  },
  // 404 page must be placed at the end !!!
  {
    path: '*',
    redirect: '/404',
    hidden: true
  }
]

const createRouter = () => new Router({
  // mode: 'history', // require service support
  scrollBehavior: () => ({
    y: 0
  }),
  routes: constantRoutes
})

const router = createRouter()

// Detail see: https://github.com/vuejs/vue-router/issues/1234#issuecomment-357941465
export function resetRouter() {
  const newRouter = createRouter()
  router.matcher = newRouter.matcher // reset router
}

export default router
